	/**
	 *
	 * @param canteen_id from 0 to 9
	 * @param isNoon indicates the time_period you want,true for noon, false for night
	 * @param minute from 0 to 119
	 * @return
	 */
	public static double getHistoryCrowdedness(int canteen_id,boolean isNoon,int minute){
		int index = canteen_id -1,noon = isNoon ? 0 : 1;
		return history_data[index * 2 + noon][minute];
	}

